"use strict";var needleHour=document.getElementById("needleHour"),needleMinute=document.getElementById("needleMinute"),needleSecond=document.getElementById("needleSecond"),setTime=function(){var e=new Date;needleSecond.style.transform="rotate(".concat(6*e.getSeconds(),"deg)"),needleMinute.style.transform="rotate(".concat(6*e.getMinutes(),"deg)"),needleHour.style.transform="rotate(".concat(30*(e.getHours()>12?e.getHours()-12:e.getHours())+.5*e.getMinutes(),"deg)")};setTime(),setInterval((function(){setTime()}),1e3);